mapper.select:
SELECT
DATE_FORMAT(vs.visit_time, '%Y-%m-%d %H:00:00') AS `time`,
DATE(vs.visit_time) AS `time`,
SUM(vs.clicks) AS clicks, SUM(vs.visitors) AS visitors FROM lu_visit_stats vs WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}
AND vs.short_url = #{shortUrl}
GROUP BY `time` ORDER BY `time`

SELECT g.country, SUM(vs.clicks) AS `count` FROM lu_visit_stats vs JOIN lu_dim_geo g ON vs.geo_id = g.id WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}
AND vs.short_url = #{shortUrl}
GROUP BY g.country ORDER BY `count` DESC

SELECT g.country, g.region, SUM(vs.clicks) AS `count` FROM lu_visit_stats vs JOIN lu_dim_geo g ON vs.geo_id = g.id WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}
AND vs.short_url = #{shortUrl}
GROUP BY g.country, g.region ORDER BY `count` DESC

SELECT g.country, g.region, g.city, SUM(vs.clicks) AS `count` FROM lu_visit_stats vs JOIN lu_dim_geo g ON vs.geo_id = g.id WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}
AND vs.short_url = #{shortUrl}
GROUP BY g.country, g.region, g.city ORDER BY `count` DESC

SELECT r.${type}        AS name,
       SUM(vs.clicks)   AS `count`
FROM lu_visit_stats vs
         JOIN lu_dim_referer r ON vs.referer_id = r.id
WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}

    AND vs.short_url = #{shortUrl}

GROUP BY r.${type}
ORDER BY `count` DESC

SELECT l.${type}        AS `name`,
       SUM(vs.clicks)   AS `count`
FROM lu_visit_stats vs
         JOIN lu_dim_locale l ON vs.locale_id = l.id
WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}

    AND vs.short_url = #{shortUrl}

GROUP BY l.${type}
ORDER BY `count` DESC

SELECT d.${type}        AS `name`,
       SUM(vs.clicks)   AS `count`
FROM lu_visit_stats vs
         JOIN lu_dim_device d ON vs.device_id = d.id
WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}

    AND vs.short_url = #{shortUrl}

GROUP BY d.${type}
ORDER BY `count` DESC

SELECT p.${type}      AS `name`,
       SUM(vs.clicks) AS `count`
FROM lu_visit_stats vs
         JOIN lu_dim_platform p ON vs.platform_id = p.id
WHERE vs.visit_time BETWEEN #{startTime} AND #{endTime}

    AND vs.short_url = #{shortUrl}

GROUP BY p.${type}
ORDER BY `count` DESC
